import React, { useState } from 'react';
import axios from 'axios';

const Signup = () => {
  const [formData, setFormData] = useState({
    fname: '',
    lname: '',
    gmail: '',
    phone: '',
    password: '',
    rePassword: '',
  });

  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState(''); // 'error' or 'success'
  const [isRegistered, setIsRegistered] = useState(false); // State to track registration success

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Handle form submission
  const handleSubmit = async () => {
    const { fname, lname, gmail, phone, password, rePassword } = formData;

    // Validate form data (validation logic not shown here)

    try {
      const response = await axios.post('http://localhost:5000/api/registration', formData);

      if (response.data.success) {
        setMessage(response.data.message); // Success message from server
        setMessageType('success'); // Set message type to success
        setIsRegistered(true); // Set the registration state to true

        // Redirect to the login page after successful registration
        setTimeout(() => {
          window.location.replace('/login'); // This will redirect to the login page
        }, 3000); // Wait for 3 seconds before redirecting (feel free to adjust)
      }
    } catch (error) {
      setMessage('Error: Unable to register');
      setMessageType('error');
    }
  };

  return (
    <div>
      <form>
        <div className="fname">
          <label htmlFor="fname">Enter First Name</label>
        </div>
        <div className="enterfname">
          <input
            type="text"
            id="fname"
            name="fname"
            value={formData.fname}
            onChange={handleChange}
          />
        </div>

        {/* Other form fields for lname, gmail, phone, password, rePassword */}

        <button type="button" onClick={handleSubmit}>Sign Up</button>
      </form>

      {message && (
        <div className={`message-box ${messageType}`}>
          <p>{message}</p>
        </div>
      )}

      {/* Optionally show a success message after registration */}
      {isRegistered && (
        <div>
          <p>Registration successful! Redirecting you to the login page...</p>
        </div>
      )}
    </div>
  );
};

export default Signup;
