import React, { useState, useEffect } from "react";
import { useSpring, animated } from "@react-spring/web";
import { RequiredValidate } from "./errorhandling";
import axios from 'axios';

const OTPVerification = ({formData,backtoregistration,backtologin}) => {
  const [step, setStep] = useState(1);
  const [otp, setOtp] = useState("");
  const [errorMsg, setErrorMsg] = useState("");
  const [successMsg, setSuccessMsg] = useState("");

  const onsendotp=()=>{

  }

 
  // Error Animation
  const errorAnim = useSpring({
    opacity: errorMsg ? 1 : 0,
    width: errorMsg ? "100%" : "0%",
    transform: errorMsg ? "scale(1)" : "scale(0.5)",
    config: { tension: 200, friction: 15 },
  });

  // Success Message Animation
  const successAnim = useSpring({
    opacity: successMsg ? 1 : 0,
    transform: successMsg ? "scale(1)" : "scale(0.5)",
    config: { tension: 200, friction: 15 },
  });

  // Auto-hide error after 1 second
  useEffect(() => {
    if (errorMsg) {
      const timer = setTimeout(() => setErrorMsg(""), 1000);
      return () => clearTimeout(timer);
    }
  }, [errorMsg]);

  // Auto-hide success message after 0.5 seconds
  useEffect(() => {
    if (successMsg) {
      const timer = setTimeout(() => setSuccessMsg(""), 500);
      return () => clearTimeout(timer);
    }
  }, [successMsg]);

  // Handle Submit
  const handleSubmit = async() => {
    const msg = RequiredValidate(otp, "OTP");
    if (msg === "") {
 
    try{
      
  const res = await axios.post('http://localhost:5000/api/verifyotp/verify-otp', { 
    gmail: formData.gmail, 
    otp: otp, // Pass the OTP as a string
  });

      if(res.status===200){

      setErrorMsg(res.data.message); // Clear previous errors
      try{
      const response = await axios.post('http://localhost:5000/api/registration/registration', formData);
      alert(response.data.message);
      if(response.status===400){
        backtoregistration();
      }
      else if(response.status===200){
        backtologin();
      }
    }
    catch(error){
      alert(error.response.data.message);
    }





     

    

      setTimeout(() => {
        setStep(2); // Move to processing step
        setTimeout(() => {
          setStep(3); // Move to OTP Verified step
        }, 3000); // Increased processing time to 3 seconds
      }, 1000); // Increased delay before processing starts to 1 second
    }
  }
  

        catch(error){
        setErrorMsg(error.res.data.message);
 }
}



  
     else {
      setErrorMsg(msg); // Show validation error
    }
  
  };

  return (
    <div style={styles.fullContainer}>
      {/* Error Message with Animation */}
      {errorMsg && (
        <animated.div style={{ ...styles.errorBox, ...errorAnim }}>
          <p>{errorMsg}</p>
        </animated.div>
      )}


      <div style={styles.container}>
        {/* OTP Input Section */}
        {step === 1 && (
          <div style={styles.box}>
            <label style={styles.label}>Enter OTP</label>
            <input
              type="text"
              name="OTP"
              id="OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              style={styles.input}
            />
            <button onClick={handleSubmit} style={styles.button}>
              Submit
            </button>
          </div>
        )}

        {/* Processing Section */}
        {step === 2 && (
          <animated.div style={styles.box}>
            <button style={styles.processingButton}>Processing...</button>
          </animated.div>
        )}

        {/* OTP Verified Section */}
        {step === 3 && (
          <animated.div style={styles.box}>
            <animated.div style={ styles.successMessage }>
              OTP Verified Successfully
            </animated.div>
          </animated.div>
        )}
      </div>
    </div>
  );
};

const styles = {
  fullContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    backgroundColor: "#4A0072",
    flexDirection: "column",
  },
  errorBox: {
    backgroundColor: "#ffcccc",
    color: "#ff0000",
    padding: "10px",
    borderRadius: "5px",
    textAlign: "center",
    fontSize: "16px",
    fontWeight: "bold",
    boxShadow: "0px 4px 10px rgba(255, 0, 0, 0.5)",
    overflow: "hidden",
  },
  successBox: {
    backgroundColor: "#ccffcc",
    color: "#008000",
    padding: "10px",
    borderRadius: "5px",
    textAlign: "center",
    fontSize: "16px",
    fontWeight: "bold",
    boxShadow: "0px 4px 10px rgba(0, 255, 0, 0.5)",
    overflow: "hidden",
  },
  container: {
    display: "flex",
    alignItems: "center",
    gap: "20px",
    padding: "20px",
    backgroundColor: "#4A0072",
    borderRadius: "12px",
  },
  box: {
    minWidth: "150px",
    padding: "30px",
    backgroundColor: "#4B4892",
    borderRadius: "20px",
    textAlign: "center",
  },
  label: {
    display: "block",
    color: "#fff",
    marginBottom: "10px",
  },
  input: {
    width: "100%",
    padding: "10px",
    borderRadius: "5px",
    border: "none",
    outline: "none",
    backgroundColor: "#ff40a0",
    color: "#fff",
    fontSize: "16px",
    marginBottom: "10px",
  },
  button: {
    backgroundColor: "#ff40a0",
    color: "#fff",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  processingButton: {
    backgroundColor: "#ff40a0",
    color: "#fff",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    fontSize: "16px",
  },
  successMessage: {
    backgroundColor: "#D9D9D9",
    color: "#000",
    padding: "10px",
    borderRadius: "5px",
    fontSize: "16px",
    fontWeight: "bold",
  },
};

export default OTPVerification;
