import React, { useState } from 'react';
import './forgetpassword.css';
import { GmailValidate } from './errorhandling';
import { useSpring, animated } from '@react-spring/web';
import axios from 'axios';

const Forgetpassword = ({ backtohome }) => {
  const [finalgmail, setgmail] = useState({ gmail: '' });

  const handleChange = (e) => {
    setgmail({ ...finalgmail, [e.target.name]: e.target.value });
  };

  const forget_checkvalidation = async () => {
    const response = await GmailValidate(finalgmail.gmail);
    // You can handle the validation response here, if necessary
    if(response){
        alert(response);
    }
    
        try{
        const server_response= await axios.post('http://localhost:5000/api/sendotp/send-otp',finalgmail.gmail);
        alert(server_response.data.message);    
    }
        catch(error){
         alert(error.server_response.data.message);
        }
    

  };

  // Spring animation code
  const div1 = useSpring({
    opacity: 1,
    transform: 'rotate(0deg)',  // Ensure correct syntax (no spaces between transform functions)
    from: {
      opacity: 0,
      transform: 'rotate(180deg)',  // Ensure transform is handled properly
    },
    config: { tension: 200, friction: 15 },
  });
  const div2 = useSpring({
    opacity: 1,
    transform: 'translateX(0%)', // Corrected syntax for translateX
    from: {
      opacity: 0,
      transform: 'translateX(-100%)', // Starts off the screen to the left
    },
    config: { tension: 70, friction: 15 },
  });
  

  return (
    <div className='forget-maincontent'>
      <animated.div className='forgetdiv1' style={div1}>
        <p onClick={backtohome}>
          >>>> GO BACK TO HOME PAGE
        </p>
      </animated.div>
      <animated.div className='forgetdiv2' style={div2}>
        <div className='forgetemail'>
          <label>Enter Your Registered Email</label>
        </div>
        <div className='inputemail'>
          <input
            type='email'
            name='gmail'
            value={finalgmail.gmail}
            onChange={handleChange}
            id='inpemail'
          />
        </div>

        <div className='forget-submit' onClick={forget_checkvalidation}>
          <p>Submit</p>
        </div>
      </animated.div>
    </div>
  );
};

export default Forgetpassword;
