import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import Gmailverification from './OTPVerification';
import Login from './Login';
import Signup from './Signup';
import Forgetpassword from './forgetpassword';

function App() {
  const [formData, setFormData] = useState({
    fname: '',
    lname: '',
    gmail: '',
    phone: '',
    password: '',
    rePassword: '',
});

  // State to toggle between login, signup, and forgetpassword
  const [page, setPage] = useState('login'); // 'login', 'signup', 'forgetpassword'

  // Function to handle page change
  const togglePage = (pageName) => {
    setPage(pageName);
  };
  const num=0;
  const incnum=()=> {
    num=num+1;
  }

  return (
    <div className="auth-container">
      {/* Conditionally render Login, Signup or Forgetpassword */}
      {page === 'login' && (
        <div className="loginpage">
          <Login onCreateAccount={() => togglePage('signup')} onforgetfunction={() => togglePage('forgetpassword')} />
        </div>
      )}
      {page === 'signup' && (
        <div className="signupage">
          <Signup formData={formData} setFormData={setFormData}  gotootp={()=>togglePage('otpverification')}/>
        </div> 
      )}
      {page === 'forgetpassword' && (
        <div className="forget-page">
          <Forgetpassword backtohome={()=>togglePage('login')} />
        </div>
      )}

{page === 'otpverification' && (
        <div className="otppage">
          <Gmailverification formData={formData} backtologin={()=>togglePage('login')}  backtoregistration={()=>togglePage('signup')}/>
        </div>
      )}
    </div>
  );
}


const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
