import React ,{useState} from 'react';
import { useSpring, animated } from '@react-spring/web';
import './Login.css';
import axios from 'axios';

const Login = ({onCreateAccount}) => {

  const [logindata, setdata]=useState({
       gmail:'',
       password:'',

  });

  // Define animations for each text
  const text1 = useSpring({
    opacity: 1,
    transform: 'translateY(0)',
    from: { opacity: 0, transform: 'translateY(-20px)' },
    delay: 500, // Delay the first text
  });

  const text2 = useSpring({
    opacity: 1,
    transform: 'translateY(0)',
    from: { opacity: 0, transform: 'translateY(-20px)' },
    delay: 1500, // Delay the second text
  });

  const text3 = useSpring({
    opacity: 1,
    transform: 'translateY(0)',
    from: { opacity: 0, transform: 'translateY(-20px)' },
    delay: 2500, // Delay the third text
  });

  const checkCredentials= async()=>{
        try{  const response= await axios.post('http://localhost:5000/api/loginauthentification',)

        }
        catch(error){

        }
  }

  return (
    <div className="main-content1">
      <div className="flex-in-main">

        <div className="flex1">
        <animated.div className="line1" style={text1}>
            <h1>Welcome</h1>
          </animated.div>
          <animated.div className="line2" style={text2}>
            <h1>To</h1>
          </animated.div>
          <animated.div className="line3" style={text3}>
            <h1>BookShoppee</h1>
          </animated.div>

          <div className="login-message">
            <div className='line4'><h1>Please</h1></div>
            <div className='line5'><h1>Login To</h1></div>
            <div className='line6'><h1>Proceed</h1></div>
    

       </div>

        </div>


        <div className="flex2"></div>

        <div className="flex3">
          <div className="Login-content">
            <div className='uname'>
            <label>Enter Gmail</label></div>
            <div className='enteruname'>
            <input type="text" name='gmail' for="uname" id="username" />
            </div>

            <div className='pass'>
            <label>Enter Password</label></div>
            <div className='enterpass'>
            <input type="password" name='password'for="pass" id="password" /><br />
            </div>

            <div className='login-button' onClick={checkCredentials}>
                <p>Login</p>
            </div>

            <div className='createnewaccount' onClick={onCreateAccount}>
                <p>Create A new Account</p>
            </div>
          
            
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
