import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion"; // ✅ Added AnimatePresence
import "./adminmain.css";

const Admin = () => {
    const [togglevalue, settogglevalue] = useState(false);

    const toggle = () => {
        settogglevalue((prev) => !prev);
    };

    // Define animation variants for sidebar & boxes
    const sidebarVariants = {
        hidden: { x: "-100%" },
        visible: { x: 0, transition: { duration: 0.4, ease: "easeOut" } },
    };

    const boxVariants = {
        hidden: { opacity: 0, scale: 0.8, y: 50 },
        visible: (i) => ({
            opacity: 1,
            scale: 1,
            y: 0,
            transition: { delay: i * 0.2, duration: 0.5, ease: "easeOut" },
        }),
    };

    return (
        <div className="admin-main">
            {/* Sidebar with Animation */}
            <AnimatePresence>
                {togglevalue && (
                    <motion.div
                        className="admin-navbar"
                        variants={sidebarVariants}
                        initial="hidden"
                        animate="visible"
                        exit="hidden"
                    >
                        <div className="togglebar" onClick={toggle}>☰</div>
                        <div className="navbar-content">
                            <div className="admin-dashboard">Dashboard</div>
                            <div className="admin-seller">Seller</div>
                            <div className="admin-user">User</div>
                            <div className="product">Product</div>
                            <div className="admin-profile">Profile</div>
                            <div className="admin-logout"><p>LOG OUT</p></div>
                        </div>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* Content Boxes with Animation */}
            <div className="display-box">
                <div className="display-box1">
                    {[ 
                        "New seller", 
                        "New Books", 
                        "Seller’s complaints", 
                        "Users complaints", 
                        "Msg to seller", 
                        "Msg to user"
                    ].map((text, i) => (
                        <motion.div
                            key={i}
                            className={`adminbox${i + 1}`}
                            custom={i}
                            variants={boxVariants}
                            initial="hidden"
                            animate="visible"
                        >
                            {text}
                        </motion.div>
                    ))}
                </div>
                <div className="display-box2"></div>
            </div>
        </div>
    );
};

export default Admin;
