import { useState } from "react";
import { motion } from "framer-motion";

export default function OTPVerification() {
  const [step, setStep] = useState(1);

  const handleSubmit = () => {
    setStep(2); // Show processing box
    setTimeout(() => {
      setStep(3); // Show verification box after 1 sec
    }, 1000);
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-purple-900">
      <div className="flex space-x-4 p-6 bg-purple-900 rounded-lg">
        
        {/* Step 1: Enter OTP */}
        <motion.div
          className="w-64 h-40 bg-blue-700 p-4 rounded-lg flex flex-col items-center justify-center space-y-4"
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
        >
          <p className="text-white">Enter OTP</p>
          <input
            type="text"
            className="w-full px-3 py-2 rounded-md bg-pink-500 outline-none"
          />
          <button
            onClick={handleSubmit}
            className="px-4 py-2 bg-pink-500 text-white rounded-md"
          >
            Submit
          </button>
        </motion.div>

        {/* Step 2: Processing (appears after submit) */}
        {step >= 2 && (
          <motion.div
            className="w-64 h-40 bg-blue-700 p-4 rounded-lg flex items-center justify-center"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
          >
            <motion.button
              className="px-6 py-3 bg-pink-500 text-white rounded-md"
              animate={{ scale: [1, 1.1, 1] }}
              transition={{ repeat: Infinity, duration: 0.8 }}
            >
              Processing
            </motion.button>
          </motion.div>
        )}

        {/* Step 3: OTP Verified (appears after 1 sec) */}
        {step === 3 && (
          <motion.div
            className="w-64 h-40 bg-blue-700 p-4 rounded-lg flex items-center justify-center"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
          >
            <div className="px-6 py-3 bg-gray-300 text-black rounded-md">
              OTP Verified Successfully
            </div>
          </motion.div>
        )}

      </div>
    </div>
  );
}
