import React, { useState } from 'react';
import './Signup.css';
import { RequiredValidate, GmailValidate, PhoneValidate, PasswordValidate, rePasswordValidate } from './errorhandling';

const Signup = () => {
  // State for form data
  const [formData, setFormData] = useState({
    fname: '',
    lname: '',
    gmail: '',
    phone: '',
    password: '',
    rePassword: '',
  });

  // State for error/success message
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState(''); // 'error' or 'success'

  // Handle form data change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Handle form submission on clicking the "Create Account" div
  const handleSubmit = () => {
    const { fname, lname, gmail, phone, password, rePassword } = formData;

    // Validate form fields
    const fnameError = RequiredValidate(fname, 'First Name');
    const lnameError = RequiredValidate(lname, 'Last Name');
    const gmailError = GmailValidate(gmail);
    const phoneError = PhoneValidate(phone);
    const passwordError = PasswordValidate(password);
    const rePasswordError = rePasswordValidate(password, rePassword);

    // Check if there are any validation errors
    if (fnameError || lnameError || gmailError || phoneError || passwordError || rePasswordError) {
      setMessage(`
        ${fnameError || ''} 
        ${lnameError || ''} 
        ${gmailError || ''} 
        ${phoneError || ''} 
        ${passwordError || ''} 
        ${rePasswordError || ''}`);
      setMessageType('error'); // Set the message type to error
    } else {
      setMessage('Form submitted successfully!');
      setMessageType('success'); // Set the message type to success
    }
  };

  return (
    <>
      <div className="main-content">
        <div className="flex1"></div>
        <div className="flex2"></div>
      </div>

      <div className="signup-content">
        <form>
          <div className="fname">
            <label htmlFor="fname">Enter First Name</label>
            <input
              type="text"
              id="fname"
              name="fname"
              value={formData.fname} // Bind the value to state
              onChange={handleChange}
            />
          </div>

          <div className="lname">
            <label htmlFor="lname">Enter Last Name</label>
            <input
              type="text"
              id="lname"
              name="lname"
              value={formData.lname} // Bind the value to state
              onChange={handleChange}
            />
          </div>

          <div className="gmail">
            <label htmlFor="gmail">Enter Gmail</label>
            <input
              type="email"
              id="gmail"
              name="gmail"
              placeholder="You@gmail.com"
              value={formData.gmail} // Bind the value to state
              onChange={handleChange}
            />
          </div>

          <div className="phone">
            <label htmlFor="phone">Phone no.</label>
            <input
              type="text"
              id="phone"
              name="phone"
              value={formData.phone} // Bind the value to state
              onChange={handleChange}
            />
          </div>

          <div className="password">
            <label htmlFor="pwd">Create A strong Password</label>
            <input
              type="password"
              id="pwd"
              name="password"
              value={formData.password} // Bind the value to state
              onChange={handleChange}
            />
          </div>

          <div className="repass">
            <label htmlFor="re-pwd">Re-enter Your password</label>
            <input
              type="password"
              id="re-pwd"
              name="rePassword"
              value={formData.rePassword} // Bind the value to state
              onChange={handleChange}
            />
          </div>

          <div className="submit" onClick={handleSubmit}>
            <p>Create Account</p>
          </div>
        </form>
      </div>

      {/* Displaying the message box */}
      {message && (
        <div className={`message-box ${messageType}`}>
          <p>{message}</p>
        </div>
      )}
    </>
  );
};

export default Signup;
