import React, { useState, useEffect } from "react";
import { useSpring, animated } from "@react-spring/web";
import { RequiredValidate } from "./errorhandling";

const OTPVerification = () => {
  const [step, setStep] = useState(1);
  const [otp, setOtp] = useState("");
  const [errorMsg, setErrorMsg] = useState("");

  // Error Animation: Expand Width, Scale, Fade
  const errorAnim = useSpring({
    opacity: errorMsg ? 1 : 0,
    width: errorMsg ? "100%" : "0%",
    transform: errorMsg ? "scale(1)" : "scale(0.5)",
    config: { tension: 200, friction: 15 },
  });

  // Auto-hide error after 1 second
  useEffect(() => {
    if (errorMsg) {
      const timer = setTimeout(() => setErrorMsg(""), 1000);
      return () => clearTimeout(timer);
    }
  }, [errorMsg]);

  // Handle Submit
  const handleSubmit = () => {
    const msg = RequiredValidate(otp);
    if (!msg) {
      setErrorMsg(""); // Clear previous errors
      setStep(2); // Show processing
      setTimeout(() => {
        setStep(3); // Show verified message
      }, 1000);
    } else {
      setErrorMsg(msg); // Show validation error
    }
  };

  return (
    <div style={styles.fullContainer}>
      {/* Error Message with Animation */}
      {errorMsg && (
        <animated.div style={{ ...styles.errorBox, ...errorAnim }}>
          <p>{errorMsg}</p>
        </animated.div>
      )}

      <div style={styles.container}>
        {/* OTP Input Section */}
        <div style={styles.box}>
          <label style={styles.label}>Enter OTP</label>
          <input
            type="text"
            name="OTP"
            id="OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            style={styles.input}
          />
          <button onClick={handleSubmit} style={styles.button}>
            Submit
          </button>
        </div>

        {/* Processing Section */}
        {step >= 2 && (
          <animated.div style={{ ...styles.box }}>
            <button style={styles.processingButton}>Processing</button>
          </animated.div>
        )}

        {/* OTP Verified Section */}
        {step >= 3 && (
          <animated.div style={{ ...styles.box }}>
            <div style={styles.successMessage}>OTP Verified Successfully</div>
          </animated.div>
        )}
      </div>
    </div>
  );
};

const styles = {
  fullContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    backgroundColor: "#4A0072",
    flexDirection: "column",
  },
  errorBox: {
    backgroundColor: "#ffcccc",
    color: "#ff0000",
    padding: "10px",
    borderRadius: "5px",
    textAlign: "center",
    fontSize: "16px",
    fontWeight: "bold",
   
    boxShadow: "0px 4px 10px rgba(255, 0, 0, 0.5)",
    overflow: "hidden", // Prevents text from overflowing while animating width
  },
  container: {
    display: "flex",
    alignItems: "center",
    gap: "20px",
    padding: "20px",
    backgroundColor: "#4A0072",
    borderRadius: "12px",
  },
  box: {
    minWidth: "150px",
    padding: "30px",
    backgroundColor: "#4B4892",
    borderRadius: "20px",
    textAlign: "center",
  },
  label: {
    display: "block",
    color: "#fff",
    marginBottom: "10px",
  },
  input: {
    width: "100%",
    padding: "10px",
    borderRadius: "5px",
    border: "none",
    outline: "none",
    backgroundColor: "#ff40a0",
    color: "#fff",
    fontSize: "16px",
    marginBottom: "10px",
  },
  button: {
    backgroundColor: "#ff40a0",
    color: "#fff",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  processingButton: {
    backgroundColor: "#ff40a0",
    color: "#fff",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    fontSize: "16px",
  },
  successMessage: {
    backgroundColor: "#D9D9D9",
    color: "#000",
    padding: "10px",
    borderRadius: "5px",
  },
};

export default OTPVerification;
