import React, { useState } from "react";
import { useSpring, animated } from "@react-spring/web";
import { RequiredValidate } from "./errorhandling";

const OTPVerification = () => {
  const [step, setStep] = useState(1);
  const[otp,setotp]=useState(null);
  const[errorvaue, errorset]=useState(false);


  // Animations for appearing next to the previous one
  const processingAnim = useSpring({
    opacity: step >= 2 ? 1 : 0,
    transform: step >= 2 ? "translateX(0px)" : "translateX(-20px)",
    config: { tension: 200, friction: 15 },
  });

  const verifiedAnim = useSpring({
    opacity: step >= 3 ? 1 : 0,
    transform: step >= 3 ? "translateX(0px)" : "translateX(-20px)",
    config: { tension: 200, friction: 15 },
  });

  // Handle Submit
  const handleSubmit = () => {
    const msg=RequiredValidate(otp);
    if(!msg){

    setStep(2); // Show processing next to OTP input
    setTimeout(() => {
      setStep(3); // After 1 second, show verified message next to processing
    }, 1000);
  }
  else{
    
  }
};

  return (
    <div style={styles.fullContainer}>
      {errorvalue &&(
        <animated.div styles={styles.errorbox}>
             
        </animated.div>
      ) }
      
      <div style={styles.container}>
        {/* OTP Input Section */}
        <div style={styles.box}>
          <label style={styles.label}>Enter OTP</label>
          <input type="text" value ={otp} style={styles.input} />
          <button onClick={handleSubmit} style={styles.button}>
            Submit
          </button>
        </div>

        {/* Processing Section (Appears after Submit) */}
        <animated.div style={{ ...styles.box, ...processingAnim }}>
          {step >= 2 && <button style={styles.processingButton}>Processing</button>}
        </animated.div>

        {/* OTP Verified Section (Appears after 1 sec) */}
        <animated.div style={{ ...styles.box, ...verifiedAnim }}>
          {step >= 3 && <div style={styles.successMessage}>OTP Verified Successfully</div>}
        </animated.div>
      </div>
    </div>
  );
};

const styles = {
  fullContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh", // Makes the div take full viewport height
    backgroundColor: "#4A0072",
  },
  container: {
    display: "flex",
    alignItems: "center",
    gap: "20px",
    padding: "20px",
    backgroundColor: "#4A0072",
    borderRadius: "12px",
  },
  box: {
    minWidth: "150px",
    padding: "30px",
    backgroundColor: "#4B4892",
    borderRadius: "20px",
    textAlign: "center",
  },
  label: {
    display: "block",
    color: "#fff",
    marginBottom: "10px",
  },
  input: {
    width: "100%",
    padding: "10px",
    borderRadius: "5px",
    border: "none",
    outline: "none",
    backgroundColor: "#ff40a0",
    color: "#fff",
    fontSize: "16px",
    marginBottom: "10px",
   
  },
  button: {
    backgroundColor: "#ff40a0",
    color: "#fff",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  processingButton: {
    backgroundColor: "#ff40a0",
    color: "#fff",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    fontSize: "16px",
  },
  successMessage: {
    backgroundColor: "#D9D9D9",
    color: "#000",
    padding: "10px",
    borderRadius: "5px",
  },
};

export default OTPVerification;
